<?phpinclude('posart/functions.php');add_theme_support('nav-menus');function comment_mail_notify($comment_id) {		$comment = get_comment($comment_id);		$parent_id = $comment->comment_parent ? $comment->comment_parent : '';		$spam_confirmed = $comment->comment_approved;		$wp_email = 'no-reply@' . preg_replace('#^www\.#', '', strtolower($_SERVER['SERVER_NAME']));		$from = "From: \"" . get_option('blogname') . "\" <$wp_email>";		$headers = "$from\nContent-Type: text/html; charset=" . get_option('blog_charset') . "\n";		if (($parent_id != '') && ($spam_confirmed != 'spam')) {			$to = trim(get_comment($parent_id)->comment_author_email);			$subject = '你在 [' . get_option("blogname") . '] 的留言有了新回复';			$message = '				<div style="background-color:#eef2fa; border:1px solid #d8e3e8; color:#111; padding:0 15px; -moz-border-radius:5px; -webkit-border-radius:5px; -khtml-border-radius:5px; border-radius:5px;">				<p><strong>' . trim(get_comment($parent_id)->comment_author) . ', 你好!</strong></p>				<p><strong>您曾在《' . get_the_title($comment->comment_post_ID) . '》的留言为:</strong><br />'				. trim(get_comment($parent_id)->comment_content) . '</p>				<p><strong>' . trim($comment->comment_author) . ' 给你的回复是:</strong><br />'				. trim($comment->comment_content) . '<br /></p>				<p>你可以点击此链接 <a href="' . htmlspecialchars(get_comment_link($parent_id)) . '">查看完整内容</a></p><br />				<p>欢迎再次来访<a href="' . get_option('home') . '">' . get_option('blogname') . '</a></p>				<p>(此邮件为系统自动发送，请勿直接回复.)</p>				</div>';			wp_mail( $to, $subject, $message, $headers );		}	}	add_action('comment_post', 'comment_mail_notify');function add_smilies_to_form(){    include(TEMPLATEPATH . '/posart/smiley.php');}add_filter( 'comment_form_after', 'add_smilies_to_form');register_nav_menus(	array(		'header_menu' => __( '顶部导航' ),	));function posart_no_nav () {	wp_list_cats('style=list');}